import React, { useState } from 'react';
import { useCart } from '../context/CartContext';
import { FaShoppingCart, FaTimes, FaTrash, FaPlus, FaMinus, FaCreditCard } from 'react-icons/fa';
import { toast } from 'react-toastify';

const Cart = () => {
  const { items, removeItem, updateQuantity, totals, clearCart } = useCart();
  const [isOpen, setIsOpen] = useState(false);
  const [promoCode, setPromoCode] = useState('');
  const [appliedPromo, setAppliedPromo] = useState(null);

  const handlePromoCode = () => {
    if (promoCode.toUpperCase() === 'AUTO10') {
      setAppliedPromo({ code: 'AUTO10', discount: 0.10 });
      toast.success('Promo code applied! 10% discount added.');
    } else {
      toast.error('Invalid promo code');
    }
  };

  const calculateFinalTotal = () => {
    let finalTotal = totals.total;
    if (appliedPromo) {
      finalTotal = totals.subtotal * (1 - appliedPromo.discount) + totals.tax + totals.shipping;
    }
    return finalTotal;
  };

  const handleCheckout = () => {
    if (items.length === 0) {
      toast.error('Your cart is empty');
      return;
    }
    toast.success('Proceeding to checkout...');
    // Here you would typically redirect to a checkout page
  };

  const cartItemCount = items.reduce((total, item) => total + item.quantity, 0);

  return (
    <>
      {/* Cart Toggle Button */}
      <button
        onClick={() => setIsOpen(true)}
        className="fixed bottom-6 right-6 bg-automotive-orange text-white p-4 rounded-full shadow-lg hover:bg-orange-600 transition-colors z-40"
      >
        <div className="relative">
          <FaShoppingCart className="text-xl" />
          {cartItemCount > 0 && (
            <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full w-6 h-6 flex items-center justify-center">
              {cartItemCount}
            </span>
          )}
        </div>
      </button>

      {/* Cart Sidebar */}
      {isOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-50">
          <div className="absolute right-0 top-0 h-full w-full max-w-md bg-white shadow-xl">
            <div className="flex flex-col h-full">
              {/* Header */}
              <div className="flex items-center justify-between p-4 border-b border-gray-200">
                <h2 className="text-xl font-semibold text-automotive-dark">
                  Shopping Cart ({cartItemCount})
                </h2>
                <button
                  onClick={() => setIsOpen(false)}
                  className="p-2 hover:bg-gray-100 rounded-full transition-colors"
                >
                  <FaTimes />
                </button>
              </div>

              {/* Cart Items */}
              <div className="flex-1 overflow-y-auto p-4">
                {items.length === 0 ? (
                  <div className="text-center py-8">
                    <FaShoppingCart className="text-6xl text-gray-300 mx-auto mb-4" />
                    <h3 className="text-lg font-semibold text-gray-600 mb-2">Your cart is empty</h3>
                    <p className="text-gray-500">Add some products to get started!</p>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {items.map(item => (
                      <div key={item.id} className="flex items-center space-x-4 p-4 bg-gray-50 rounded-lg">
                        <img
                          src={item.image}
                          alt={item.name}
                          className="w-16 h-16 object-cover rounded-lg"
                        />
                        <div className="flex-1">
                          <h4 className="font-semibold text-automotive-dark">{item.name}</h4>
                          <p className="text-sm text-gray-600">{item.vendor}</p>
                          <p className="text-automotive-blue font-semibold">${item.price.toFixed(2)}</p>
                        </div>
                        <div className="flex items-center space-x-2">
                          <button
                            onClick={() => updateQuantity(item.id, item.quantity - 1)}
                            className="p-1 hover:bg-gray-200 rounded"
                          >
                            <FaMinus className="text-xs" />
                          </button>
                          <span className="w-8 text-center">{item.quantity}</span>
                          <button
                            onClick={() => updateQuantity(item.id, item.quantity + 1)}
                            className="p-1 hover:bg-gray-200 rounded"
                          >
                            <FaPlus className="text-xs" />
                          </button>
                          <button
                            onClick={() => removeItem(item.id)}
                            className="p-1 hover:bg-red-100 text-red-500 rounded"
                          >
                            <FaTrash className="text-xs" />
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>

              {/* Cart Summary */}
              {items.length > 0 && (
                <div className="border-t border-gray-200 p-4 space-y-4">
                  {/* Promo Code */}
                  <div className="flex space-x-2">
                    <input
                      type="text"
                      placeholder="Promo code"
                      value={promoCode}
                      onChange={(e) => setPromoCode(e.target.value)}
                      className="flex-1 px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-automotive-orange"
                    />
                    <button
                      onClick={handlePromoCode}
                      className="btn-primary text-sm"
                    >
                      Apply
                    </button>
                  </div>

                  {/* Applied Promo */}
                  {appliedPromo && (
                    <div className="bg-green-50 p-3 rounded-lg">
                      <p className="text-green-800 text-sm">
                        Promo code {appliedPromo.code} applied! ({appliedPromo.discount * 100}% off)
                      </p>
                    </div>
                  )}

                  {/* Totals */}
                  <div className="space-y-2">
                    <div className="flex justify-between">
                      <span>Subtotal:</span>
                      <span>${totals.subtotal.toFixed(2)}</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Tax (7%):</span>
                      <span>${totals.tax.toFixed(2)}</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Shipping:</span>
                      <span>{totals.shipping === 0 ? 'FREE' : `$${totals.shipping.toFixed(2)}`}</span>
                    </div>
                    {appliedPromo && (
                      <div className="flex justify-between text-green-600">
                        <span>Discount:</span>
                        <span>-${(totals.subtotal * appliedPromo.discount).toFixed(2)}</span>
                      </div>
                    )}
                    <div className="border-t pt-2 flex justify-between font-semibold text-lg">
                      <span>Total:</span>
                      <span>${calculateFinalTotal().toFixed(2)}</span>
                    </div>
                  </div>

                  {/* Action Buttons */}
                  <div className="space-y-2">
                    <button
                      onClick={handleCheckout}
                      className="w-full btn-primary flex items-center justify-center space-x-2"
                    >
                      <FaCreditCard />
                      <span>Proceed to Checkout</span>
                    </button>
                    <button
                      onClick={clearCart}
                      className="w-full btn-secondary"
                    >
                      Clear Cart
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Cart;
